from fastapi import *
from fastapi.responses import RedirectResponse

def internal(app: FastAPI):

    router = APIRouter(
        prefix = "/internal",
        tags = ["internal"],
        responses = { 404: {"description": "Not found"} },
        include_in_schema = False
    )

    def cdn():
        internals = {
            "discord": "https://img.shields.io/discord/1083716853928558652?style=for-the-badge&logo=discord&logoColor=ffffff&label=DISCORD&color=5865F2",
            "license": "https://img.shields.io/github/license/IceCreamMC/IceCream?style=for-the-badge&logo=github&logoColor=ffffff",
            "watches": "https://img.shields.io/github/watchers/IceCreamMC/IceCream?label=watchers&logo=data:image/png;base64,&style=flat-square&color=green",
            "stars": "https://img.shields.io/github/stars/IceCreamMC/IceCream?label=stars&logo=data:image/png;base64,&style=for-the-badge&color=green",
            "forks": "https://img.shields.io/github/forks/IceCreamMC/IceCream?label=forks&logo=data:image/png;base64,&style=flat-square&color=green",
            "bstats": "https://bstats.org/signatures/server-implementation/IceCreamMC.svg",
            "downloads": "https://img.shields.io/github/downloads/IceCreamMC/IceCream/total?label=Downloads&logo=data:image/png;base64,&style=for-the-badge&color=green",
            "translate": "https://badges.crowdin.net/icecreammc-document-portal/localized.svg",
            "dismini": "https://img.shields.io/discord/1083716853928558652?style=flat-square&logo=discord&logoColor=ffffff&label=Discord&color=5865F2",
            "gitmini": "https://img.shields.io/github/stars/IceCreamMC?style=flat-square&logo=github&label=Followers&color=yellow",
            "mailini": "https://img.shields.io/badge/-info@icecreammc.xyz-EA4335?style=flat-square&logo=gmail&logoColor=fcfcfc"
        }

        @router.get("/cdn/{badge:str}", status_code = 308)
        async def cdn(badge: str):
            if badge not in internals:
                return { 404: {"description": "Invalid badge"} }

            return RedirectResponse(internals[badge])
    cdn()

    app.include_router(router)
